// Test script to debug login issues
const axios = require('axios');

const BASE_URL = 'http://localhost:5000/api/v1/user';

async function testLogin(email, password) {
    console.log(`\n Testing login with: ${email}`);
    console.log('━'.repeat(50));

    try {
        const response = await axios.post(`${BASE_URL}/login`, {
            email: email,
            password: password
        }, {
            headers: {
                'Content-Type': 'application/json'
            }
        });

        console.log('✅ LOGIN SUCCESSFUL!');
        console.log('User:', response.data.user);
        console.log('Access Token:', response.data.accessToken?.substring(0, 50) + '...');
        return true;
    } catch (error) {
        console.log('❌ LOGIN FAILED!');
        if (error.response) {
            console.log('Status:', error.response.status);
            console.log('Message:', error.response.data.message);
        } else {
            console.log('Error:', error.message);
        }
        return false;
    }
}

async function testRegister(fullName, email, password) {
    console.log(`\n Testing registration: ${email}`);
    console.log('━'.repeat(50));

    try {
        const response = await axios.post(`${BASE_URL}/register`, {
            fullName: fullName,
            email: email,
            password: password
        }, {
            headers: {
                'Content-Type': 'application/json'
            }
        });

        console.log('✅ REGISTRATION SUCCESSFUL!');
        console.log('User:', response.data.user);
        return true;
    } catch (error) {
        console.log('❌ REGISTRATION FAILED!');
        if (error.response) {
            console.log('Status:', error.response.status);
            console.log('Message:', error.response.data.message);
        } else {
            console.log('Error:', error.message);
        }
        return false;
    }
}

async function runTests() {
    console.log(' Language Hub Login Test Suite');
    console.log('='.repeat(50));

    // Test 1: Login with default admin
    await testLogin('admin@gmail.com', 'Admin12345');

    // Test 2: Login with wrong password
    await testLogin('admin@gmail.com', 'WrongPassword');

    // Test 3: Login with non-existent user
    await testLogin('nonexistent@gmail.com', 'SomePass123');

    // Test 4: Register new user
    await testRegister('Test User', 'test@example.com', 'TestPass123');

    // Test 5: Login with newly registered user
    await testLogin('test@example.com', 'TestPass123');

    console.log('\n' + '='.repeat(50));
    console.log(' Tests completed!');
}

// If email/password provided as command line args, test those
const args = process.argv.slice(2);
if (args.length >= 2) {
    const [email, password] = args;
    testLogin(email, password);
} else {
    runTests();
}
